INSERT INTO `website_news` VALUES (6, '学习贯彻习近平总书记关于安全生产重要指示批示精神 切实维护职工生命安全和身心健康', '全总安全生产工作领导小组会议今天在京召开。会议深入学习贯彻习近平总书记关于安全生产重要指示批示精神，传达学习全国安全生产电视电话会议精神，分析当前工会安全生产面临的形势任务，对进一步提高全总和各级工会安全生产工作水平提出要求。全总党组书记、副主席、书记处第一书记，全总安全生产工作领导小组组长徐留平主持会议并讲话。', '/profile/upload/2025/06/28/R-C_20250628171919A061.jpg', '江西荣欣防爆电器有限公司', '1', '2022-03-27 00:00:00', 0, '0', '1', '', , '0', 'admin', '2025-06-28 17:17:11', 'admin', '2025-06-28 17:19:20', NULL, 0);
INSERT INTO `website_news` VALUES (7, '国家能源局关于进一步加快煤矿智能化建设促进煤炭高质量发展的通知', '为深入贯彻落实党中央、国务院决策部署，加快发展新质生产力，推进数智技术与煤炭产业深度融合，进一步提升煤矿智能化建设水平，促进煤炭高质量发展，现通知如下。', '/profile/upload/2025/06/28/69bb2e07c9124a028b288311181a935a_20250628172317A062.jpeg', '江西荣欣防爆电器有限公司', '1', '2024-05-21 00:00:00', 0, '0', '1', '', , '0', 'admin', '2025-06-28 17:23:19', 'admin', '2025-06-29 14:49:09', NULL, 2);
INSERT INTO `website_news` VALUES (8, '我司已于6月10日至11日通过国家安标中心评审', '近日，江西荣欣防爆电器有限公司迎来重要发展里程碑。6月10日至11日，公司顺利通过国家安全生产监督管理总局矿用产品安全标志中心（以下简称“国家安标中心”）的严格评审，标志着公司在矿用防爆电器领域的产品质量、技术实力及生产管理体系再次获得国家级权威认可。', '/profile/upload/2025/06/29/damen_20250629144556A020.jpg', '江西荣欣防爆电器有限公司', '0', '2025-06-16 00:00:00', 0, '0', '0', '', , '0', 'admin', '2025-06-29 14:45:30', 'admin', '2025-06-29 15:04:46', NULL, 100);
